				ctx.save();
				ctx.globalAlpha = alpha - 0.15;
				var sc = 0.2;
				ctx.drawImage(TextureManager.Get("bubble"), x- this.width * sc, y - this.height * sc, this.width * (scale + 2 * sc), this.height * (scale + 2 * sc));
				ctx.restore();
