	static GetPointDistanceFromLine(point, lineStart, lineEnd)
	{
		var a = (lineEnd[1] - lineStart[1]) / (lineEnd[0] - lineStart[0]);
		var b = -(a * lineStart[0]) + lineStart[1];

		var A = a;
		var B = -1;
		var C = b;

		return Math.abs(A * point[0] + B * point[1] + C) / Math.sqrt(A* A + B * B);
	}

