        var location = locations[locationsId.indexOf(id)];
        var entities = location.RoomInfo.EntityTypes;
        var bosses = location.RoomInfo.BossTypes;
        var entityList = World.EntityList;

        var rank = locationInfo[id].rank ?? 'F';

