		// 	shotCount: 16,
		// 	shotDelayMin: 8,
		// 	shotDelayMax: 10
		// },
		// {
		// 	Texture: "bullet_enemy",
		// 	Scale: 2
		// }));
		
		this.HitBox.Scale = .5;
		this.attack_frame = 0;

		this.LootTable = new LootTable([
			new LootTableItemData("Cannon2", 5, 1, 1)
		]);

        this.WingAnimation = new Transition(0, 4, .15, true, 0, 0, false);
