	{
		super.Update();

		if(this.isLayingEgg)
		{
			this.allowMove = false;
			this.allowRotationChange = false;
			this.Rotation = 0;
		}
		else
		{
			this.allowMove = true;
			this.allowRotationChange = true;
		}

		if(this.isEnraged) this.EnrageUpdate();
	}


